		public void toggleEmergencyMarker(){
			
			//if eaShow=true
			if(eaShow){
				//hide all the emergency area markers
				for(Marker m :emergencyArea){
					m.setVisible(false);
				}
				eaShow = false;
			}
			//else if eaShow=false
			else{
				//show all the emergency area markers
				for( Marker m : emergencyArea){
					m.setVisible(true);
				}
				eaShow = true;
			}
		}
		
